export function convertFileToBlob({ file }) {
    return new Promise((resolve, reject) => {
        try{
            const xhr = new XMLHttpRequest();
            xhr.onload = function() {
                resolve(xhr.response);
            }; 
            xhr.onerror = function(e) {
                // console.log(e);
                reject({ error: true });
            };
            xhr.responseType = 'blob';
            xhr.open('GET', file, true);
            xhr.send(null);
        } catch (error) {
            console.log(error);
            reject({ error: true });
        }     
    });
}
