import firebase from "firebase";
import "firebase/firestore";

import * as auth from "../../services/auth";

const type = "auth";

const actions = {
  CHECK_AUTHORIZATION: type + "CHECK_AUTHORIZATION",

  LOGIN_REQUEST: type + "LOGIN_REQUEST",
  LOGIN_VERIFICATION_SUCCESS: type + "LOGIN_VERIFICATION_SUCCESS",
  LOGIN_TAC_CONFIRMATION : type + "LOGIN_TAC_CONFIRMATION",
  LOGIN_SUCCESS: type + "LOGIN_SUCCESS",
  LOGIN_ERROR: type + "LOGIN_ERROR",

  SIGNUP_REQUEST: type + "SIGNUP_REQUEST",
  SIGNUP_SUCCESS: type + "SIGNUP_SUCCESS",
  SIGNUP_ERROR: type + "SIGNUP_ERROR",

  LOGOUT: type + "LOGOUT",

  FORGOT_PASSWORD_REQUEST: type + "FORGOT_PASSWORD_REQUEST",
  FORGOT_PASSWORD_SUCCESS: type + "FORGOT_PASSWORD_SUCCESS",
  FORGOT_PASSWORD_ERROR: type + "FORGOT_PASSWORD_ERROR",

  MODAL_CONTROL: type + "MODAL_CONTROL",
  UPDATE: type + "UPDATE",
  BASIC_INFO_UPDATA : type + "BASIC_INFO_UPDATA",
};

export const COLLECTION_USERS = "users";

export const modalControl = () => {
  return { 
    type: actions.MODAL_CONTROL 
  };
};

export const update = data => {
  return {
    type: actions.UPDATE,
    payload: { data }
  };
};

export const loginWithPhoneNumber = ({ phoneNumber, recaptchaToken }) => {
  return dispatch => {
    dispatch({ type: actions.LOGIN_REQUEST });
    return new Promise((resolve, reject) => {
      auth
        .login(auth.providers.PHONE, { phoneNumber, recaptchaToken })
        .then(confirmationResult => {
          resolve(confirmationResult);
          dispatch(loginVerificationSuccess({ confirmationResult }));
        })
        .catch(error => {
          console.log(error);
          reject(error);
          dispatch(loginError({error}));
        });
    });
  };
};

export const loginVerificationSuccess = ({confirmationResult}) => {
  return dispatch => {
    dispatch({
      type: actions.LOGIN_VERIFICATION_SUCCESS,
      payload: { data: confirmationResult }
    });
  };
};

export const loginTACConfirmation = ({tac})=>{
  return (dispatch, getState) => {
    dispatch({ type: actions.LOGIN_TAC_CONFIRMATION });
    return new Promise(async (resolve, reject) => {
      try{
        
        const { confirmationResult } = getState().Auth.loginDetails
        const {user} = await confirmationResult.confirm(tac)
        resolve(user)
        dispatch(loginSuccess({ user }));
      }catch(error){
        console.log(error)
        reject(error)
        dispatch(loginError({ error }));
      }
    });
  };
}

export function loginSuccess({ user }) {
  return dispatch => {
    auth.storeUserToLocal(user).catch((error)=>{
      console.log(error)
    });
    dispatch({
      type: actions.LOGIN_SUCCESS,
      payload: { user }
    });
  };
}

export function loginError({ error }) {
  return dispatch => {
    dispatch({
      type: actions.LOGIN_ERROR,
      payload: { error }
    });
  };
}

export const logout = () => {
  return dispatch => {
    auth.removeUserFromLocal().catch((error)=>{
      console.log(error)
    });
    auth.logout();
    dispatch({ type: actions.LOGOUT });
  };
};

export function checkAuthorization() {
  return dispatch => {
    dispatch({ type: actions.CHECK_AUTHORIZATION });
    return new Promise(async (resolve, reject) => {
      try {
        let user = await auth.getUserFromLocal();
        if (user) {
          dispatch(loginSuccess({user}));
        } else {
          dispatch(logout());
        }
        resolve(user);
        auth.isAuthenticated(user => {
          let isLoggedIn = user !== null;
          if (isLoggedIn) {
            dispatch(loginSuccess({user}));
          } else {
            dispatch(logout());
          }
        });
      } catch (error) {
        dispatch(loginError({error}));
        console.log(error);
        reject(error);
      }
    });
  };
}

export default actions;
