import { StyleSheet } from "react-native";
import {
    padding,
    Colors,
    fontSize,
    fontFamily,
    windowWidth,
    normalize
} from "../../../settings/styles/theme";

const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: "center",
      padding: 20,
      paddingTop: 60
    },
    inputStyle: {
      color: "#000",
      paddingRight: 10,
      paddingLeft: 5,
      fontSize: 16,
      lineHeight: 23,
      borderBottomColor: "#555",
      borderBottomWidth: 0.5,
      paddingBottom: 3
    },
    labelStyle: {
      fontSize: 16,
      paddingLeft: 10,
      color: "#000"
    },
    errorStyle: {
      fontSize: 12,
      color: "#f00",
      paddingLeft: 10
    },
    cameraButton: {
      borderWidth: 4,
      position: "absolute",
      padding: 5,
      borderColor: Colors.WHITE,
      borderRadius: 50,
      right: 10,
      top: 200
    },
    headerBackground: {
      width: "100%",
      justifyContent: "center",
      paddingVertical: 50
    },
    headerLogo: {
      width: 150,
      height: 120,
      alignSelf: "center"
    },
    checkBox: {
      backgroundColor: "#fff",
      borderColor: "#fff",
      flex: 1
    },
    modal_selector: {
      paddingTop: 10
    }
  });

export default styles;
