import actions from "./action";

const initialState = {
  permissionVerificationLoading : false,
  permissionVerificationError: false,
  permissions:{},

  loading: false,
  tags: {},
  categories: {},

  readLoading: false,
  readError: false,
  shops: [],
  shop: {
    id: null,
    title: null,
    displayTitle: null,
    subtitle: null,
    description: null,
    logo: [],
    images: [],
    facebookUrl: null,
    instagramUrl: null,
    whatsapp:null,
    websiteUrl: null,
    phoneNumber: null,
    email: null,
    address: {
      line1: null,
      line2: null,
      postcode: null,
      state: null,
      country: null
    },
    operatingHour: [
      { close: "1800", day: "mon", open: "0600", operate: false },
      { close: "1800", day: "tue", open: "0600", operate: false },
      { close: "1800", day: "wed", open: "0600", operate: false },
      { close: "1800", day: "thu", open: "0600", operate: false },
      { close: "1800", day: "fri", open: "0600", operate: false },
      { close: "1800", day: "sat", open: "0600", operate: false },
      { close: "1800", day: "sun", open: "0600", operate: false }
    ],
    merchants: [],
    manager: [],
    supervisor: [],
    worker: [],
    tags: [],
    categories: [],
    dateJoined: null,
    totalMissions: 0,
    created: { at: null, by: null },
    deleted: { at: null, by: null },
    updated: { at: null, by: null },
    l: { _lat: 0, _long: 0 },
    g: null
  }
};

const shopsReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case actions.PERMISSION_VERIFICATION:
      return { ...state, permissionVerificationLoading: true, permissionVerificationError: false };

    case actions.PERMISSION_VERIFICATION_SUCCESS:
      return { ...state, permissionVerificationLoading: false, permissions: payload.data.permissions };

    case actions.PERMISSION_VERIFICATION_ERROR:
      return { ...state, permissionVerificationLoading: false, permissionVerificationError: payload.error };

    case actions.READ_FROM_DATABASE:
      return { ...state, loading: true };

    case actions.READ_FROM_DATABASE_SUCCESS:
      return { ...state, loading: false };

    case actions.READ_FROM_DATABASE_ERROR:
      return { ...state, loading: false };

    case actions.READ_RECORD:
      return { ...state, readLoading: true, readError: false };

    case actions.READ_RECORD_SUCCESS:
      return {
        ...state,
        readLoading: false,
        shop: payload.data,
      };

    case actions.READ_RECORD_ERROR:
      return { ...state, readLoading: false, readError: payload.error };

    default:
      return state;
  }
};

export default shopsReducer;
