import React from "react";
import styles from "./styles";

import {
    View,
    FlatList,
    TouchableOpacity,
    Text,
} from "../../../../marslab-library-react-native/components/atoms";

import {
    ImageInfo,
    IconButton
} from "../../../../marslab-library-react-native/components/molecules";

const LuckyDrawRanking = ({
   data,
   routeTitle,
   routeDescription,
   routeEndedTitle,
   eventTitle,
   luckyTitle,
   drawTitle,
   onEventPress,
   onSingleEventPress
})=> {
 
    return(
        <View style={styles.container}>
            <FlatList
                data={data}
                renderItem={({ item, index }) => (
                <View>
                    <TouchableOpacity 
                        style={styles.row}
                        onPress={onSingleEventPress}
                    >
                        <Text style={styles.index}>
                            {index + 1}
                        </Text>
                        <View style={styles.imageContainer}>
                            <ImageInfo
                                banner={item.image}
                                imageContainer={styles.image}
                                imageStyle={styles.imageFrame}
                            />
                        </View>
                        <View style={styles.titleContainer}>
                            <Text style={styles.title}>
                                {routeTitle} {index + 1}
                            </Text>
                            <Text>
                                {routeDescription}
                            </Text>
                        </View>
                        <View>
                            <Text style={styles.subtitle}>
                                {routeEndedTitle}
                            </Text>
                            <Text style={styles.index}>
                                {item.date}
                            </Text>
                        </View>
                    </TouchableOpacity>
                    <View style={styles.line} />
                </View>
                )}
                keyExtractor={(item) => item.id}
            />
            <View style={styles.buttonContainer}>
                <IconButton
                    iconName='ios-calendar'
                    iconSize={25}
                    iconColor='#ccc'
                    iconContainer={styles.iconActiveButtonContainer}
                    title={eventTitle}
                    titleStyle={styles.iconActiveText}
                    onPress={onEventPress}
                />
                <IconButton
                    iconName='ios-calendar'
                    iconSize={25}
                    iconColor='white'
                    iconContainer={styles.iconButtonContainer}
                    title={luckyTitle}
                    subtitle={drawTitle}
                    titleStyle={styles.iconText}
                />
            </View>
        </View>
    );
};

export { LuckyDrawRanking };
