import { StyleSheet, Platform} from 'react-native';
import { 
    padding, 
    color, 
    fontSize, 
    fontFamily, 
    windowWidth, 
    normalize 
}  from "../../../settings/styles/theme"

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#FAFAFA',
    },
    subContainer1: {
        flex: 1,
        height: 270,
    },
    subContainer2: {
        flex: 1,
        //paddingLeft: 22,
    },
    subContainer3: {
        flex: 1,
        marginBottom: 10,
    },
    subContainer4: {
        flex: 1,
        marginBottom: 10,
        marginTop: -10,
    },
//--top--------------------------------------------
    actionDotStyle: {
        backgroundColor: '#999999',
        width: 6,
        height: 6,
        borderRadius: 7,
        marginLeft: 7,
        marginRight: 7
    },
    dotStyle:{
        backgroundColor: '#ffffff',
        width: 6,
        height: 6,
        borderRadius: 7,
        marginLeft: 7,
        marginRight: 7
    },
    paginationStyle:{
        bottom: 20
    },
    imageTopStyle:{
        width: '100%', 
        height: "100%"
    },
//--second-------------------------------------------- 
    sectionTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 7,
        marginTop: "5%",
        marginLeft: 20,
        fontFamily: "RobotoRegular"
    },
    cardSection: {
        margin: 0,
        padding: 0,
        flexDirection: 'row',
        borderRadius: 4,
        width:"100%", 
    },
    cardTitle: {
        fontSize: 16,
        fontWeight: 'bold',
        marginBottom: 13,
        fontFamily: "RobotoRegular"
    },
    cardContent: {
        color: '#595959',
        fontSize: 14,
        marginLeft: 10,
        fontFamily: "RobotoRegular"
        //marginBottom: 3,
    },
    firstCardStyle: {
        width: 332, 
        borderRadius: 5,
        marginRight: 20,
        marginLeft: 20,
        shadowColor: "#000",
        shadowOffset: {	width: 0, height: 0,},
        shadowOpacity: 0.22,
        shadowRadius: 2.22,
        elevation: 3,
        
    },
    cardStyle: {
        width: 332, 
        borderRadius: 5,
        marginRight: 20,
        marginLeft: 0,
        shadowColor: "#000",
        shadowOffset: {	width: 0, height: 0,},
        shadowOpacity: 0.22,
        shadowRadius: 2.22,
        elevation: 3,
    },
    imageMap: {
        width: 131,
        height: 150,
        borderTopLeftRadius: 3,
        borderBottomLeftRadius: 3,
        resizeMode: 'stretch'
    },
    cardHolder: {
        flexDirection: 'row', 
        backgroundColor: '#FAFAFA', 
        marginTop: 0,
    },
    textHolderStyle: {
        flex: 1, 
        padding: 11,
    },
    iconStyle:{
        marginTop: 0,
        marginRight: -5,
        alignItems: 'flex-end',
        height: 0,
    },
    //-----------------------------------------------------
    imageMap2: {
        /* width: 363.2,
        height: 131.2, */
        borderRadius: 3,
        resizeMode: "cover"
    },
    cardSection2: {
        margin: 0,
        padding: 0,
        flexDirection: 'column',
        borderRadius: 4,
    },
    cardTitle2: {
        fontSize: 25,
        fontWeight: 'bold',
        color: "#ffffff",
        textAlign: "center",
        margin: 10,
        fontFamily: "RobotoRegular"
    },
    textHolderStyle2: { 
        flex: 1, 
        //padding: 64,
        alignItems: "center", 
        alignContent: "center",
        justifyContent: "center",
        backgroundColor: 'rgba(0, 0, 0, 0.5)', 
        borderRadius: 4,
    },
    imageBackgroundStyle: {
        resizeMode: "cover",
        height: 131.2,
    },
});

export default styles;
