import * as t from "./actions";

const INITIAL_STATE = {
    isBooted: false,
    updateNeeded: false,
    forceUpdate: false,
    OS: null,
    appVersion: "0.0.0",
    isDevice: false,
    android: {
        latestVersion: "0.0.0",
        latestVersionDescription: null,
        playstoreUrl: null
    },
    ios: {
        latestVersion: "0.0.0",
        latestVersionDescription: null,
        appstoreUrl: null
    },
    error: null,
    notification_loading: false,
    notification_error: false,
    notification_listenning: false,
    notification_token: null,
    system_information_from_database_reading: false,
    system_information_from_database_read_error: false
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case t.APP_ON_BOOTED:
            return {
                ...state,
                isBooted: true
            };
        case t.APP_ON_BOOT_FAILED:
            return {
                ...state,
                error: action.error,
                isBooted: true
            };
        case t.Third_PARTY_SERVICE_INIT:
            return {
                ...state
            };
        case t.SYSTEM_INFORMATION_FROM_DEVICE_READ:
            return {
                ...state,
                ...action.systemInfo
            };
        case t.SYSTEM_INFORMATION_FROM_DATABASE_READING:
            return {
                ...state,
                system_information_from_database_read_error: false,
                system_information_from_database_reading: true
            };
        case t.SYSTEM_INFORMATION_FROM_DATABASE_READ_SUCCESS:
            return {
                ...state,
                system_information_from_database_read_error: false,
                system_information_from_database_reading: false,
                ...action.systemInfo
            };
        case t.SYSTEM_INFORMATION_FROM_DATABASE_READ_FAILED:
            return {
                ...state,
                system_information_from_database_read_error: action.error,
                system_information_from_database_reading: false
            };

        case t.NOTIFICATION_INIT_LOADING:
            return {
                ...state,
                notification_loading: true,
                notification_error: false
            };
        case t.NOTIFICATION_INIT_LOADED:
            const { token } = action;
            return {
                ...state,
                notification_token: token,
                notification_loading: false,
                notification_error: false
            };

        case t.NOTIFICATION_INIT_LOAD_FAILED:
            return {
                ...state,
                notification_token: null,
                notification_loading: false,
                notification_error: action.error
            };
        case t.NOTIFICATION_START:
            return {
                ...state,
                notification_listenning: true
            };

        case t.NOTIFICATION_CLOSED:
            return {
                ...state,
                notification_loading: false,
                notification_error: false,
                notification_listenning: false
            };
        default:
            return state;
    }
};
