import * as database from "../../utils/helper/database";

export function readOwnInfo({uid}) {
    const ref = `userPrivate0/${uid}`;
    return new Promise((resolve, reject) => {
        database
            .readData({ref})
            .then(user => {
                resolve(user);
            })
            .catch(error => {
                reject(error);
            });
    });
}
