import React, { Component } from "react";
import { connect } from "react-redux";

import { Keyboard, Alert, Platform } from "react-native";

import {
  View,
  Image,
  ImageBackground,
  KeyboardAvoidingView,
  Text,
  ScrollView,
  TouchableOpacity,
  TouchableWithoutFeedBack
} from "../../../marslab-library-react-native/components/atoms";
import { TACTextInput } from "../../../marslab-library-react-native/components/molecules";
import styles from "./styles";

import { SuccessfulModal } from "./successful";

import onLogIn from "../../../routes/onLogIn";
import { update, loginTACConfirmation } from "../../../marslab-library-react-native/redux/auth/actions";
import clone from "clone";

class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      countdown: 59
    };
  }

  onRecordChange = (key, value) => {
    let { loginDetails } = clone(this.props);
    if (key) loginDetails[key] = value;
    this.props.update(loginDetails);
  };

  onLoginButtonPress(tac) {
    const {loginTACConfirmation} = this.props
    loginTACConfirmation({tac})
  }

  render() {
    const { loginDetails, loading } = this.props;
    const { tac } = loginDetails;

    return (
      <ImageBackground
        source={require("../../../../assets/Login.jpg")}
        style={styles.container}
      >
        <TouchableWithoutFeedBack onPress={Keyboard.dismiss} accessible={false}>
          <KeyboardAvoidingView
            //behavior={Platform.OS === "ios" ? "padding" : null}
            behavior="position"
            style={styles.container2}
            keyboardVerticalOffset={Platform.OS === "ios" ? -200 : null}
            enabled
          >
            <ScrollView showsVerticalScrollIndicator={false}>
              <View style={styles.containerpart1}>
                <Image
                  source={require("../../../assets/gogogain/login_logo.png")}
                  style={styles.logoImage}
                />
              </View>
              <View style={styles.containerpart2}>
                <Text style={styles.subTitle}>
                  Enter the 6 digit code we sent you via phone number to
                  continue
                </Text>
              </View>
              <View style={styles.containerpart3}>
                <TACTextInput
                  value={tac}
                  onChange={this.onRecordChange.bind(this, "tac")}
                />
              </View>
              <View style={styles.containerpart4}>
                <Text style={styles.countDownText}>code expired in: </Text>
                <Text style={styles.countDownTime}>
                  00:{this.state.countdown}
                </Text>
              </View>

              <View style={styles.containerpart5}>
                <TouchableOpacity
                  style={styles.submitContainer}
                  onPress={this.onLoginButtonPress.bind(this, tac)}
                >
                  <Text style={[styles.submitText]}>Verify</Text>
                </TouchableOpacity>
              </View>
              <View>
                <SuccessfulModal />
              </View>
            </ScrollView>
          </KeyboardAvoidingView>
        </TouchableWithoutFeedBack>
      </ImageBackground>
    );
  }
}

const mapStateToProps = ({ Auth }) => {
  const { loginDetails, loading, error } = Auth;

  return { loginDetails, loading, error };
};

export default connect(mapStateToProps, {
  update,
  loginTACConfirmation
})(index);
