import React, { Component } from "react";
import { connect } from "react-redux";
import { Actions } from "react-native-router-flux";
import * as Location from "expo-location";

import { verifyPermission, loadShops } from "../../../redux/shops/action";

import styles from "./styles";
import { Colors } from "../../../settings/styles/theme";
import {
  View,
  FlatList,
  TouchableOpacity,
  Image,
  Text
} from "../../../marslab-library-react-native/components/atoms";
import {
  Card,
  CardSection
} from "../../../marslab-library-react-native/components/molecules";
import ModalSelector from "react-native-modal-selector";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import Icon from "react-native-vector-icons/FontAwesome";

const ITEMS_PER_PAGE = 10;

function Item({
  name,
  shopID,
  logo,
  picture=[],
  address,
  subscribers,
  distance,
  endDate,
  index,
  onPress,
  onSubscribePress,
  subscribed
}) {
  const { image, title, detail, subscribe, profile } = styles;

  let cover = "";
  if (picture.length === 0 ) cover = require("../../../../assets/404NotFound800x533.jpg");
  else cover = { uri: picture[0] };

  let icon = "";
  if ( logo.length === 0) icon = require("../../../../assets/logo.png");
  else icon = { uri: logo[0] };

  return (
    <TouchableOpacity onPress={onPress}>
      <Card style={{ width: "98%" }}>
        <CardSection
          style={{
            borderBottomWidth: 0,
          }}
        >
          <Image style={image} resizeMode="cover" source={cover} />
        </CardSection>
        <CardSection 
          style={{
            paddingHorizontal: 15,
            borderBottomWidth: 0,
          }}
        >
          <Text style={title}>{name}</Text>
        </CardSection>
        <CardSection
          style={{
            paddingHorizontal: 15,
            borderBottomWidth: 0,
          }}
        >
          <MaterialCommunityIcons name="map-marker-distance" size={16} />
          <Text style={detail}>
            Just {+(Math.round(distance + "e+2") + "e-2")} Km away
          </Text>
        </CardSection>

        <CardSection
          style={{
            paddingHorizontal: 15,
            borderBottomWidth: 0,
            marginBottom: 10
          }}
        >
          <Icon name="location-arrow" size={16} />
          <Text style={detail}>
            {address.line1} {address.line2}
          </Text>
        </CardSection>
        {/* <TouchableOpacity
          style={subscribe}
          onPress={() => onSubscribePress(subscribed, shopID, index)}
        >
          <Text
            style={{
              color: "#fff",
              fontSize: 16,
              fontWeight: "bold"
            }}
          >
            {subscribed ? "Unsubscribe" : "Subscribe"}
          </Text>
        </TouchableOpacity> */}
        <TouchableOpacity style={profile}>
          <Image
            style={{
              width: 50,
              height: 50,
              borderRadius: 50
            }}
            source={icon}
          />
        </TouchableOpacity>
      </Card>
    </TouchableOpacity>
  );
}

// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
class index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      dataSource: [],
      filteredData: [],
      page: 0,
      // isLoading:false,
      isRefreshing: false,
      tags: props.tags,
      categories: props.categories,
      selectedCategory: {},
      selectedTag: "All" //default all tag selected
    };
    this.onSubscribePressed = this.onSubscribePressed.bind(this);
    this.handleLoadMore = this.handleLoadMore.bind(this);
    this.handleRefresh = this.handleRefresh.bind(this);
    this.renderFooter = this.renderFooter.bind(this);
    this.filterData = this.filterData.bind(this);
  }

  // old ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  componentDidMount = async () => {
    this.props.verifyPermission().then(permissions => {
      if (permissions.location !== "granted") {
        alert("Permission to access location is necessary");
      } else this.handleRefresh();
    });
  };

  handleRefresh = async () => {
    const radius = 50;
    let i = 1;

    this.setState({ isRefreshing: true });
    let location = await Location.getCurrentPositionAsync({});
    do {
      await this.props
        .loadShops({
          radius: radius * i,
          latitude: location.coords.latitude,
          longtitude: location.coords.longitude,
          selectedCategory: this.state.selectedCategory.id
            ? this.state.selectedCategory.id
            : null
        })
        .then(Data => {
          this.setState({ dataSource: Data, page: 0, data: [] });
          Data.length !== 0 && this.filterData();
        })
        .catch(error => {
          alert(error);
        });
      i = i * 3;
    } while (this.state.dataSource.length === 0 && radius * i < 1000);
    this.setState({
      isRefreshing: false
      //isLoading:false
    });
    i = 1;
    //console.log(this.state.dataSource.length)
  };

  handleLoadMore() {
    //this.setState({isLoading:true})
    const { page, data } = this.state;
    const start = page * ITEMS_PER_PAGE;
    const end = (page + 1) * ITEMS_PER_PAGE;

    const newData = this.state.filteredData.slice(start, end); // here, we will receive next batch of the items
    this.setState({ data: [...data, ...newData], page: page + 1 }); // here we are appending new batch to existing batch
  }

  filterData() {
    const filteredData =
      this.state.selectedTag !== "All"
        ? this.state.dataSource.filter(item => {
            return item.tags
              .map(tag => {
                return tag;
              })
              .includes(this.state.selectedTag);
          })
        : this.state.dataSource;
    const dataDisplay = [];
    filteredData.map(data => {
      dataDisplay.push({
        ...data
        //subscribed: this.props.subscribedList.includes(data.id)
      });
    });
    this.setState({ filteredData: dataDisplay, page: 0, data: [] });
    this.handleLoadMore();
  }

  renderFooter() {
    return (
      // this.state.isLoading?
      // <View style={styles.loader}>
      //     <ActivityIndicator size="large"/>
      // </View> :null
      Platform.OS === "ios" ? (
        <Card style={{ backgroundColor: "transparent" }}>
          <CardSection style={styles.emptySection}>
            <Icon name="inbox" size={64} style={styles.emptyIcon} />
          </CardSection>
        </Card>
      ) : (
        <Card style={{ backgroundColor: "transparent", elevation: 0 }}>
          <CardSection style={[styles.emptySection, { elevation: 0 }]}>
            <Icon name="inbox" size={64} style={styles.emptyIcon} />
          </CardSection>
        </Card>
      )
    );
  }

  onMerchantPressed(item) {
    Actions.SingleMerchant({ merchant: item });
  }

  onSubscribePressed = async (status, shopID, index) => {
    const data = this.state.data;
    //console.log(this.props.idToken)
    let subscribedList = this.props.subscribedList;

    if (!status) {
      subscribedList.push(shopID);
      this.props.updateSubscribeList({ subscribedList });
      data[index].subscribed = true;
      this.setState({ data: data });

      fetch(`${FIREBASE_CLOUD_FUNCTION}/subscribes/subscribe`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
          Accept: "application/json",
          Authorization: "Bearer" + this.props.idToken
        },
        body: JSON.stringify({ shopID })
      })
        .then(async response => {
          const responseJson = await response.json();

          if (response.status === 200) {
          } else {
            console.log("Subscribe error :" + JSON.stringify(responseJson));
          }
        })
        .catch(error => {
          console.error(error);
        });
    } else {
      subscribedList = subscribedList.filter(item => item !== shopID);
      this.props.updateSubscribeList({ subscribedList });
      data[index].subscribed = false;
      this.setState({ data: data });

      fetch(`${FIREBASE_CLOUD_FUNCTION}/subscribes/unsubscribe`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
          Accept: "application/json",
          Authorization: "Bearer" + this.props.idToken
        },
        body: JSON.stringify({ shopID })
      })
        .then(async response => {
          const responseJson = await response.json();

          if (response.status === 200) {
          } else {
            console.log("unSubscribe error :" + JSON.stringify(responseJson));
          }
        })
        .catch(error => {
          console.error(error);
        });
    }
  };

  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

  render() {
    return (
      <View>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "flex-end",
            marginBottom: 8
          }}
        >
          <ModalSelector
            onChange={option => {
              this.setState({ selectedCategory: option, selectedTag: "All" });
              this.handleRefresh();
            }}
            data={this.state.categories}
            initValue="Select Categories"
            style={styles.categoriesButton}
            selectStyle={styles.categoriesSelect}
            selectTextStyle={styles.modalSelectTextStyle}
            optionTextStyle={styles.modalOptionTextStyle}
            keyExtractor={item => item.id}
            labelExtractor={item => item.title}
          />

          <ModalSelector
            data={this.state.selectedCategory.tags}
            keyExtractor={item => item}
            labelExtractor={item =>
              this.state.tags
                .filter(tag => tag.id === item)
                .map(({ title }) => title)[0]
            }
            onChange={option => {
              this.setState({ selectedTag: option });
              this.filterData();
            }}
            selectStyle={styles.categoriesSelect}
            selectTextStyle={styles.modalSelectTextStyle}
            optionTextStyle={styles.modalOptionTextStyle}
            style={{ justifyContent: "center" }}
          >
            <Icon name="filter" size={20} style={styles.tagsButton} />
          </ModalSelector>
        </View>
        <FlatList
          data={this.state.data}
          renderItem={({ item, index }) => (
            <Item
              onPress={() => this.onMerchantPressed(item)}
              onSubscribePress={this.onSubscribePressed}
              name={item.displayTitle}
              logo={item.logo}
              picture={item.images}
              address={item.address}
              subscribers={item.subscribers}
              subscribed={item.subscribed}
              distance={item.distance}
              shopID={item.id}
              index={index}
            />
          )}
          keyExtractor={item => item.id}
          onRefresh={this.handleRefresh}
          refreshing={this.state.isRefreshing}
          onEndReached={this.handleLoadMore}
          onEndReachedThreshold={0.5}
          ListFooterComponent={this.renderFooter}
          style={styles.flatList}
          extraData={this.state}
        />
      </View>
    );
  }
}

const mapStateToProps = state => {
  const { categories, tags } = state.Settings;
  const { uid } = state.Auth.user;
  const { subscribedList, idToken } = state.Auth;

  return { categories, tags, uid, idToken, subscribedList };
};

export default connect(mapStateToProps, { verifyPermission, loadShops })(index);
