import React from "react";
import {
    Text,
    View,
    Avatar,
    Button,
    Icon
} from "../../atoms";

import styles from "./styles";

const UserInfoHeader = ({
    avatarSource,
    onAvatarPress,
    userName,
    userPhoneNumber,
    userEmail,
    isUserVerified,
    onEditProfilePress
}) => {
    return (
            <View style={styles.container}>
                <View style={styles.avatarContainer}>
                    <Avatar
                        rounded
                        size="xlarge"
                        onPress={onAvatarPress}
                        source={avatarSource}
                    />
                </View>
                <View>
                    <View style={styles.textContainer}>
                        <Text style={styles.userTitle}>{userName}</Text>
                        <Icon
                            size={15}
                            iconStyle={[
                                styles.userInfomationIcon,
                                { borderWidth: 0 }
                            ]}
                            containerStyle={{ justifyContent: "center" }}
                            name={isUserVerified ? "verified" : "unverified"}
                            type="octicon"
                            color={styles.userInfomationIcon.borderColor}
                        />
                    </View>

                    <View style={styles.textContainer}>
                        <Icon
                            //raised
                            size={15}
                            iconStyle={styles.userInfomationIcon}
                            containerStyle={{
                                justifyContent: "center",
                                alignItems: "center",
                                alignContent: "center",
                                alignSelf: "center"
                            }}
                            name="phone"
                            type="font-awesome"
                            color={styles.userInfomationIcon.borderColor}
                        />
                        <Text style={styles.userDesc}>
                            {userPhoneNumber
                                ? userPhoneNumber
                                : "Verification needed"}
                        </Text>
                    </View>

                    <View style={styles.textContainer}>
                        <Icon
                            //raised
                            size={15}
                            iconStyle={styles.userInfomationIcon}
                            containerStyle={{
                                justifyContent: "center",
                                alignItems: "center"
                            }}
                            name="email"
                            type="material"
                            color={styles.userInfomationIcon.borderColor}
                        />
                        <Text style={styles.userDesc}>{userEmail}</Text>
                    </View>
                    
                    <View>
                        <Button
                            title={
                                isUserVerified ? "Edit Profile" : "Verify Now"
                            }
                            type="outline"
                            titleStyle={styles.titleButton}
                            buttonStyle={styles.editButton}
                            containerStyle={styles.editButtonContainer}
                            raised
                            onPress={onEditProfilePress}
                        />
                    </View>
                </View>
            </View>
    );
};

export { UserInfoHeader };
