import React from "react";
import styles from "./styles";
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    FlatList,
    VirtualizedList
} from "../../atoms";
import {
    Card,
} from "../../molecules";
import ContentLoader, { Rect } from 'react-content-loader/native'

const SmallCardList = ({
 title,
 dataSource,
 onPress,
 noImage,
 readLoading,
})=>{
    const DATA = []; 

    let arrangeDataSource = dataSource.sort((a, b) => {
        return new Date(b.createAt) - new Date(a.createAt);
    });

    const getItem = (data, index) => {
        return {
            key: "advertisementLoading" + index
        }
    }

    const CardListLoading = ({index}) =>{
        return (
            <Card
                key = {"advertisementCardLoading" + index}
                style={ index === 0 ? styles.firstNewsCardStyle :  styles.newsCardStyle }
            > 
                <ContentLoader 
                    speed={1}
                    width={400}
                    height={170}
                    backgroundColor= "#d9d9d9"
                >   
                    <Rect x="0" y="0" rx="4" ry="4" width="160" height="110" /> 
                    <Rect x="10" y="120" rx="4" ry="4" width="140" height="20" /> 
                </ContentLoader>
            </Card>
        );
    }


    const CardList = ({item, index}) =>{

        if(item.deleted_at === null){
            return(
                <TouchableOpacity onPress={()=>onPress(item.id)} >
                    <Card 
                        key={item.id}
                        style={ index === 0 ? styles.firstNewsCardStyle : styles.newsCardStyle }
                    >
                        {item.coverPic !== "" ? 
                            <Image source={{uri:item.coverPic}} style={styles.newsImage} resizeMode = "stretch" />
                        : 
                            <Image source={noImage} style={styles.newsImage}/>
                        }
                        <Text numberOfLines={2} style={styles.newsTitleTextStyle}>{item.title}</Text>
                    </Card>
                </TouchableOpacity>
            ); 
        }else{
            return null
        }
    }


    return(
        <View style={styles.subContainer3}>
            <View>
                <Text style={styles.sectionTitle}>{title}</Text>
            </View>
            <View>
                    {readLoading ?
                        <VirtualizedList 
                            horizontal
                            showsHorizontalScrollIndicator = {false}
                            data={DATA}
                            renderItem={({index}) => <CardListLoading index={index}/>}
                            keyExtractor={item => item.key}
                            getItemCount={() => {return 3}}
                            getItem={getItem}
                        />
                    :
                        <FlatList 
                            horizontal
                            showsHorizontalScrollIndicator = {false}
                            data = {arrangeDataSource}
                            keyExtractor={item => item.id}
                            renderItem={({ item, index }) => <CardList item={item} index={index}/> }
                        />
                    } 
            </View>
        </View>
    );
};
               
export { SmallCardList };
