import React from "react";
import styles from "./styles";

import {
    View,
    FlatList,
    TouchableOpacity,
    Text,
} from "../../../../marslab-library-react-native/components/atoms";

import {
    ImageInfo,
} from "../../../../marslab-library-react-native/components/molecules";

const RouteRanking = ({
   data,
   prizeTitle,
   completeInTitle,
   message,
   termsAndConditions
})=> {
 
    return(
        <View style={styles.container}>
            <FlatList
                data={data}   
                showsVerticalScrollIndicator={false}         
                renderItem={({ item, index }) => (
                <View>
                    {item.name === 'You' ?
                    <View style={styles.rowSelected}>
                    {/* <TouchableOpacity style={styles.rowSelected}> */}
                        <Text style={styles.indexSelected}>
                            {index + 1}
                        </Text>
                        <View style={styles.imageContainer}>
                            <ImageInfo
                                banner={item.image}
                                imageContainer={styles.image}
                                imageStyle={styles.imageFrame}
                            />
                        </View>
                        <View style={styles.titleContainer}>
                            <Text style={styles.titleSelected}>
                                {item.name}
                            </Text>
                            <Text style={styles.prizeTitle}>
                                {prizeTitle}{item.prize}
                            </Text>
                        </View>
                        <View>
                            <Text style={styles.subtitle}>
                                {completeInTitle}
                            </Text>
                            <Text style={styles.indexSelected}>
                                {item.date}
                            </Text>
                        </View>
                    {/* </TouchableOpacity> */}
                    </View>
                    :
                    <View style={styles.row}>
                    {/* <TouchableOpacity style={styles.row}> */}
                        <Text style={styles.index}>
                            {index + 1}
                        </Text>
                        <View style={styles.imageContainer}>
                            <ImageInfo
                                banner={item.image}
                                imageContainer={styles.image}
                                imageStyle={styles.imageFrame}
                            />
                        </View>
                        <View style={styles.titleContainer}>
                            <Text style={styles.title}>
                                {item.name}
                            </Text>
                            <Text style={styles.prizeTitle}>
                                {prizeTitle}{item.prize}
                            </Text>
                        </View>
                        <View>
                            <Text style={styles.subtitle}>
                                {completeInTitle}
                            </Text>
                            <Text style={styles.index}>
                                {item.date}
                            </Text>
                        </View>
                    {/* </TouchableOpacity> */}
                    </View>
                    }
                    <View style={styles.line} />
                </View>
                )}
                keyExtractor={(item) => item.id}
            />
            <View style={styles.messageContainer}>
                <Text>
                    {message}
                </Text>
                <Text style={styles.textColor}>
                    {termsAndConditions}
                </Text>
            </View>
        </View>
    );
};

export { RouteRanking };
