import React from "react";
import {
    ScrollView,
    RefreshControl,
} from "../../../marslab-library-react-native/components/atoms";

import {
    UserInfoHeader,
    MyEventCard,
    MenuList
} from "../../../marslab-library-react-native/components/organisms"

import styles from "./styles";

import Constants from "expo-constants"; // 'expo-constants' with the latest SDK to date

const UserProfile = ({
    user,
    onEventPress,
    onHelpPress,
    onEditProfilePress,
    onSettingsPress,
    onAvatarPress,
    isRefreshing,
    refreshHandler,
}) => {
    return (
        <ScrollView
            style={styles.scroll}
            refreshControl={
            <RefreshControl
                refreshing={isRefreshing}
                onRefresh={refreshHandler}
            />
            }
        >
            <UserInfoHeader 
                avatarSource={user.avatar}
                onAvatarPress={onAvatarPress}
                userName={user.name}
                userPhoneNumber={user.phoneNumber}
                userEmail={user.email}
                isUserVerified={user.verified}
                onEditProfilePress={onEditProfilePress}
            />

            <MyEventCard 
                onEventPress={onEventPress}
            />
            
            <MenuList 
                title="Account"
                listItem={{
                    title: "Points",     
                    rightTitle: user.points !== undefined ? user.points.toString() : "Relogin",
                    rightTitleStyle: styles.rightTitle,
                    containerStyle: styles.listItemContainer,
                    leftIconContainerStyle: styles.moreIcon,
                    leftIcon: {
                        size: 24,
                        color: "white",
                        type: "material-community",
                        name: "coin"
                    }
                }}
            />

            <MenuList 
                title="More"
                listItem={
                    [{
                        title: "Need help",
                        onPress: onHelpPress,
                        containerStyle: styles.listItemContainer,
                        rightIcon: {
                            size: 24,
                            color: "#eaeaea",
                            name: "chevron-right",
                            type: "entypo"
                        },
                        leftIconContainerStyle: styles.moreIcon,
                        leftIcon: {
                            size: 24,
                            color: "white",
                            type: "antdesign",
                            name: "customerservice"
                        }
                    },
                    {
                        title: "Settings",
                        onPress: onSettingsPress,
                        containerStyle: styles.listItemContainer,
                        rightIcon: {
                            size: 24,
                            color: "#eaeaea",
                            name: "chevron-right",
                            type: "entypo"
                        },
                        leftIconContainerStyle: styles.moreIcon,
                        leftIcon: {
                            size: 24,
                            color: "white",
                            type: "ionicon",
                            name: "md-settings"
                        }
                    },
                    {
                        title: "Version",
                        rightTitle: Constants.manifest.version,
                        containerStyle: styles.listItemContainer,
                        leftIconContainerStyle: styles.moreIcon,
                        leftIcon: {
                            size: 24,
                            color: "white",
                            type: "entypo",
                            name: "info"
                        }
                    }]
                }
            />
        </ScrollView>
    );
};

export { UserProfile };
