import React from "react";
import styles from "./styles";
import {
    View,
    ScrollView,
    Image,
    Text,
    ImageBackground,
    TouchableOpacity,
    FlatList,
    VirtualizedList
} from "../../../marslab-library-react-native/components/atoms";
import {
    Card,
    CardSection,
} from "../../../marslab-library-react-native/components/molecules";
import {
    SmallCardList,
    ImageSwiper
} from "../../../marslab-library-react-native/components/organisms";
import moment from "moment";
import ContentLoader, { Rect } from 'react-content-loader/native'
import FontAwesome from "react-native-vector-icons/FontAwesome";

export default({
    slider,
    dataSource,
    dataSource2,
    sectionTitle1,
    sectionTitle2,
    sectionTitle3,
    label1,
    label2,
    label3,
    unit,
    onPressCard,
    advertisements,
    onPressAdvertisement,
    noImageAdvertisement,
    noImageHeaderSlider,
    readLoadingAdvertisement,
    readLoadingRoute,
    readLoadingRouteTicket,
    readLoadingHeaderImages,
})=> {
    const DATA = []

    const getItem = (data, index) => {
        return {
            key: "routeTicketsOrRoutesLoading" + index
        }
    }

    const CardListLoading = ({index}) => {
        return(
            <Card 
                key={"cardLoading" + index} 
                style={ index === 0 ? styles.firstCardStyle : styles.cardStyle}
            >
                <ContentLoader 
                    speed={1}
                    width={400}
                    height={130}
                    backgroundColor= "#d9d9d9"
                >   
                    <Rect x="0" y="0" rx="4" ry="4" width="332" height="130" /> 
                    
                </ContentLoader>
            </Card>
        );
    }

    const NoChallengesCardList = ({index, data}) => {
        return (
            <TouchableOpacity onPress={()=>onPressCard(data.id)} >
                <Card 
                    key={data.id} 
                    style={ index === 0 ? styles.firstCardStyle : styles.cardStyle}
                >
                    <CardSection  style={styles.cardSection}>
                        <Image source={data.image} style={styles.imageMap} />
                        <View style = {styles.textHolderStyle}>
                            {data.joined == 1 &&
                                <View style={styles.iconStyle}>
                                    <FontAwesome
                                        name='circle'
                                        color='#65FF03'
                                        size={10}
                                    />
                                </View>
                            }
                            <Text style={styles.cardTitle}>{data.title} ({data.type})</Text>
                            <Text style={styles.cardContent}>{label1}{data.totalMission}</Text>
                            <Text style={styles.cardContent}>{label2}{data.seatLeft}{unit}</Text>
                            <Text style={styles.cardContent}>{label3}{moment(data.startTime).format("D MMM")} - {moment(data.endTime).format("D MMM YYYY")}</Text>
                        </View>
                    </CardSection>
                </Card>
            </TouchableOpacity>
        );
    }

    const ChallengesCardList = ({index, data}) => {
        return (
            <TouchableOpacity onPress={()=>onPressCard(data.id)} >
                <Card 
                    key = {data.id} 
                    style={ index === 0 ? styles.firstCardStyle : styles.cardStyle}
                >
                    <CardSection  style={styles.cardSection2}>
                        <ImageBackground
                            source={data.image}
                            imageStyle={styles.imageMap2} 
                            style={styles.imageBackgroundStyle}
                        >
                        <View style = {styles.textHolderStyle2}>
                            <Text style={styles.cardTitle2}>{data.title} ({data.type})</Text>
                        </View>
                        </ImageBackground>
                    </CardSection>
                </Card>
            </TouchableOpacity>
        );
    }

    return(
        <ScrollView contentContainerStyle={{flexGrow:1}}>
            <View style={styles.container}>
                
                <View style={styles.subContainer1}>
                    {readLoadingHeaderImages ?
                        <ContentLoader 
                            speed={1}
                            height={280}
                            backgroundColor= "#d9d9d9"
                        >   
                            <Rect x="0" y="0" rx="4" ry="4" width="100%" height="280" /> 
                        </ContentLoader>
                    :
                        <ImageSwiper 
                            style={styles}
                            slider={slider}
                            autoplayTime={5}
                            autoplay={true}
                            noImageSlider={noImageHeaderSlider}
                            condition={slider.length > 0}
                        />
                    }
                </View>

                {readLoadingRoute ? 
                    <View style={styles.subContainer2}>
                        <View>
                            <Text style={styles.sectionTitle}>{sectionTitle1}</Text>
                        </View>
                        <VirtualizedList 
                            horizontal
                            showsHorizontalScrollIndicator = {false}
                            data={DATA}
                            renderItem={({index}) => <CardListLoading index={index}/>}
                            keyExtractor={item => item.key}
                            getItemCount={() => {return 3}}
                            getItem={getItem}
                        />
                    </View>
                :
                    dataSource2.length != 0 ?
                        <View style={styles.subContainer2}>
                            <View>
                                <Text style={styles.sectionTitle}>{sectionTitle1}</Text>
                            </View>
                            <FlatList 
                                horizontal
                                showsHorizontalScrollIndicator = {false}
                                data = {dataSource2}
                                keyExtractor={item => item.id}
                                renderItem={({ item, index }) => <NoChallengesCardList data={item} index={index}/> }
                            />
                        </View>
                    :
                        <View style={styles.subContainer2}></View>
                }

                {advertisements.length<0? 
                    <View style={styles.subContainer3}></View>
                :
                    <SmallCardList 
                        title = {sectionTitle2}
                        dataSource = {advertisements}
                        onPress = {onPressAdvertisement}
                        noImage = {noImageAdvertisement}
                        readLoading = {readLoadingAdvertisement}
                    />
                }

                {readLoadingRouteTicket ? 
                    null
                :
                    dataSource.length != 0 ?
                        <View style={styles.subContainer4}>
                            <View>
                                <Text style={styles.sectionTitle}>{sectionTitle3}</Text>
                            </View>
                            <FlatList 
                                horizontal
                                showsHorizontalScrollIndicator = {false}
                                data = {dataSource}
                                keyExtractor={item => item.id}
                                renderItem={({ item, index }) => <ChallengesCardList data={item} index={index}/> }
                            />
                        </View>
                    :
                        null
                }

            </View>
        </ScrollView>
    );
};
