import * as Location from "expo-location";

export function getCurrentLocation() {
  return new Promise(async (resolve, reject) => {
    try {
      const location = await Location.getCurrentPositionAsync({});
      resolve(location);
    } catch (error) {
      reject(error);
    }
  });
}
