import { backend } from "../../utils/helper";

const objectName = "User";

export function unionNotisToken({ data : {notificationToken} }) {
  const apiName = `http${objectName}UnionNotisToken`;
  return new Promise((resolve, reject) => {
    const data = {
      notificationToken,
    };
    backend
      .callApi({
        apiName,
        data,
      })
      .then((result) => {
        resolve(result);
      })
      .catch((error) => {
        reject(error);
      });
  });
}

export function removeNotisToken({ data: {notificationToken} }) {
  const apiName = `http${objectName}RemoveNotisToken`;
  return new Promise((resolve, reject) => {
    backend
      .callApi({
        apiName,
        data: {
          notificationToken,
        },
      })
      .then((result) => {
        resolve(result);
      })
      .catch((error) => {
        reject(error);
      });
  });
}
