import React from "react";
import styles from "./styles";
import {
    View,
    Image,
    Swiper,
} from "../../atoms";


const ImageSwiper = ({
    style,
    slider,
    autoplayTime,
    autoplay,
    noImageSlider,
    condition
})=>{
    return(
        <Swiper 
            autoplay={autoplay}
            autoplayTimeout={autoplayTime} 
            activeDot={ <View style={style.actionDotStyle}/> }
            dot={ <View style={style.dotStyle} /> }
            paginationStyle={style.paginationStyle}
            index = {0}
        >
            {condition ?
                slider.map((data, index) => {
                    return(
                        <View key = {"imageSlider"+index} style={style.subContainer1}>
                            <Image
                                source={{uri:data}}
                                style={style.imageTopStyle}
                                resizeMode="stretch"
                            />
                        </View>
                        
                    );
                })
            :
                <View style={style.subContainer1}>
                    <Image
                        source={noImageSlider}
                        style={style.imageTopStyle}
                        resizeMode="stretch"
                    />
                </View>
            }
        </Swiper>
    );
};

export { ImageSwiper };
