import React from 'react';
import { View, Text, TouchableOpacity } from '../atoms';
import Icon from "react-native-vector-icons/Ionicons";
import { Platform } from "react-native";

const Header = ({
    title,
    subtitle,
    subtitleDescription,
    iconName,
    iconColor,
    iconSize,
    subContainer,
    titleStyle,
    subtitleStyle,
    onPress
}) => {
    return (
        iconName ?
            <View style={styles.container}>
                <Text style={styles.textTitle}>
                    {title}
                </Text>
                <TouchableOpacity onPress={onPress}>
                    <View style={styles.subContainer}>
                        <View style={styles.iconContainer}>
                            <Icon name={iconName} color={iconColor} size={iconSize} />
                        </View>
                        <Text style={styles.textSubtitle}>
                            {subtitle}
                        </Text>
                    </View>  
                </TouchableOpacity>        
            </View>
        :
            <View style={subContainer}>
                <Text style={titleStyle}>
                    {title}
                </Text>
                <Text style={subtitleStyle}>
                    {subtitle}{subtitleDescription}
                </Text>
            </View>
    );
};

const styles = {
    container: {
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 10,
        marginHorizontal: 30,
        paddingVertical: 5
    },
    textTitle: {
        fontWeight: 'bold',
        fontSize: 20,
    },
    textSubtitle: {
        fontWeight: 'bold',
        color: '#f18a22',
        textTransform: 'uppercase',
        fontSize: 16,
        paddingTop: 2,
        paddingLeft: 5
    },
    subContainer: {
        display: 'flex',
        flexDirection: 'row',
    },
    iconContainer: {
        paddingTop: Platform.OS === 'ios' ? 2 : 4,
    }
};

export { Header };
