import React, { Component } from "react";
import { StyleSheet } from "react-native";
import { View, Text, TextInput } from "../../../marslab-library-react-native/components/atoms";
import { FONT_WEIGHT_BOLD } from "../../../settings/styles/theme/typography";

const InputTextField = props => {

    const { inputTitle, input } = styles;

    return (
        <View style={props.style}>
            <Text style={inputTitle}>{props.title}</Text>
            <TextInput
                placeholder={props.placeholderText}
                secureTextEntry={props.isSecure}
                style={[input, props.inputStyle]}
                autoCorrect={false}
                onChangeText={props.onChangeText}
                value={props.value}
                placeholderTextColor = {props.placeholderTextColor}
            />
            {props.underline ? (
                <View
                    style={{
                        borderBottomColor: "#D8D8D8",
                        borderBottomWidth: 1
                    }}
                />
            ) : null}
        </View>
    );
};

const styles = StyleSheet.create({
    inputTitle: {
        color: "#fff",
        fontSize: 18,
        fontWeight: 'bold',
        position: 'absolute',
        top: 11,
        left: 10,
    },
    input: {
        paddingVertical: 12,
        color: "#fff",
        fontSize: 16,
        //fontFamily: "Avenir Next",
        justifyContent: "center",
        paddingLeft: 20,
    }
});

export { InputTextField };
