import React from "react";
import { Platform } from "react-native";
import { View, FlatList, TouchableOpacity, Text } from "../../atoms";
import { Card, CardSection, IconButton, CardLabel } from "../../molecules";
import ContentLoader, { Rect } from "react-content-loader/native";
import Icon from "react-native-vector-icons/FontAwesome";
import moment from "moment";

import styles from "./styles";

const RouteAreaList = ({
  totalMissionTitle,
  periodTitle,
  seatLeftTitle,
  completeTitle,
  winnerTitle,
  periodEndTitle,
  prizeTitle,
  completeInTitle,
  data,
  routeTitle,
  onSingleRouteMapPress,
  style,
  readLoading,
}) => {

  if (readLoading)
    return (

      <View style={style.container}>
        <Card style={style.card}>
          <View style={style.rowZero}>
            <ContentLoader
              speed={1}
              width={"100%"}
              height={35}
              backgroundColor="#d9d9d9"
            >
              <Rect x="0" y="8" rx="5" ry="5" width="90%" height="28" />
            </ContentLoader>
          </View>
          <View style={style.rowOne}>
            <ContentLoader
              speed={1}
              width={"100%"}
              height={35}
              backgroundColor="#d9d9d9"
            >
              <Rect x="0" y="8" rx="5" ry="5" width="50%" height="28" />
            </ContentLoader>
          </View>
          <View style={style.line} />
          <View style={style.rowTwo}>
            <ContentLoader
              speed={1}
              width={"100%"}
              height={20}
              backgroundColor="#d9d9d9"
            >
              <Rect x="0" y="0" rx="5" ry="5" width="40%" height="16" />
            </ContentLoader>
          </View>
          <View style={style.rowThree}>
            <ContentLoader
              speed={1}
              width={"100%"}
              height={18}
              backgroundColor="#d9d9d9"
            >
              <Rect x="0" y="0" rx="5" ry="5" width="50%" height="16" />
            </ContentLoader>
          </View>
        </Card>
      </View>


      
    );
  else
    return data[0] === undefined ? (
      Platform.OS === "ios" ? (
        <Card style={{ backgroundColor: "transparent", elevation: 0 }}>
          <CardSection style={[styles.emptySection, { elevation: 0 }]}>
            <Icon name="inbox" size={64} style={styles.emptyIcon} />
          </CardSection>
        </Card>
      ) : (
        <Card style={{ backgroundColor: "transparent", elevation: 0 }}>
          <CardSection style={[styles.emptySection, { elevation: 0 }]}>
            <Icon name="inbox" size={64} style={styles.emptyIcon} />
          </CardSection>
        </Card>
      )
    ) : (
      <FlatList
        data={data}
        renderItem={({ item, index }) => (
          <TouchableOpacity onPress={() => onSingleRouteMapPress(item)}>
            <View style={style.container}>
              <Card
                style={[
                  style.card,
                  item.routeTicketId
                    ? {
                        borderWidth: 1,
                        borderColor: "#FF9100",
                        borderBottomColor: "#FF9100",
                        borderBottomWidth: 1,
                      }
                    : {},
                ]}
              >
                <CardLabel
                  containerStyle={style.subContainerIcon}
                  iconStyle={style.icon}
                  iconName="circle"
                  iconColor={item.ongoing.at ? "#65FF03" : "#F6F805"}
                  iconSize={15}
                />

                <View style={style.rowZero}>
                  <Text style={style.routeNumber}>{item.title}</Text>
                  <IconButton
                    iconName={
                      item.routeTicketId ? "md-arrow-forward" : "ios-lock"
                    }
                    //iconName="md-arrow-forward"
                    iconColor="#f18a22"
                    iconSize={28}
                  />
                </View>
                <View style={style.rowOne}>
                  <Text style={style.areaName}>{item.type}</Text>
                </View>
                <View style={style.line} />
                <View style={style.rowTwo}>
                  {item.winner.displayName ? (
                    <CardLabel
                      containerStyle={style.subContainer}
                      label={winnerTitle}
                      labelValue={item.winner.displayName}
                      textStyle={style.textColor}
                    />
                  ) : (
                    <CardLabel
                      containerStyle={style.subContainer}
                      label={totalMissionTitle}
                      labelValue={item.totalMissions}
                      textStyle={style.textColor}
                    />
                  )}
                    <CardLabel
                      containerStyle={style.subContainer}
                      label={
                        item.winner.displayName
                          ? completeInTitle 
                          : item.ongoing.at !== null
                          ? completeTitle
                          : item.pending.at && seatLeftTitle
                      }
                      labelValue={
                        item.winner.displayName
                          ? item.winner.at.from(item.startTime, true)
                          : item.ongoing.at !== null
                          ? item.numberCompletedMissions +
                            " / " +
                            item.totalMissions
                          : item.pending.at &&
                            item.currentUser <= item.minimumUser
                          ? item.minimumUser - item.currentUser
                          : 0
                      }
                      textStyle={style.textColor}
                    />
                </View>
                <View style={style.rowThree}>
                  {item.winner.prizeTitle ? (
                    <CardLabel
                      containerStyle={style.subContainer}
                      label={prizeTitle}
                      labelValue={item.winner.prizeTitle}
                      textStyle={style.textColor}
                    />
                  ) : item.ongoing.at ? (
                    <CardLabel
                      containerStyle={style.subContainer}
                      label={periodEndTitle}
                      labelValue={moment(item.endTime).format("DD MMM YYYY")}
                      textStyle={style.textColor}
                    />
                  ) : (
                    item.pending.at && (
                      <CardLabel
                        containerStyle={style.subContainer}
                        label={periodTitle}
                        labelValue={moment(item.startTime).format("DD MMM")}
                        labelValue2=" - "
                        labelValue3={moment(item.endTime).format("DD MMM YYYY")}
                        textStyle={style.textColor}
                      />
                    )
                  )}
                </View>
              </Card>
            </View>
          </TouchableOpacity>
        )}
        keyExtractor={(item) => item.id}
      />
    );
};

export { RouteAreaList };
