import React from "react";
import styles from "./styles";

import { 
    View, 
    Text, 
} from "../../../../marslab-library-react-native/components/atoms";

import { IconButton } from "../../../../marslab-library-react-native/components/molecules";

import { Camera } from 'expo-camera';

const PaymentCameraReceipt = ({
    hasPermission,
    cameraType,
    cameraConfig,
    onPress
})=> {
 
    if (hasPermission === null) {
        return <View />;
    } else if (hasPermission === false) {
        return <Text style={styles.textFontFamily}>No access to camera</Text>;
    } else {
        return (
            <View style={styles.container}>
                <Camera style={styles.container} type={cameraType} ref={cameraConfig}>
                    <View style={styles.cameraContainer}>
                        <IconButton
                            iconContainer={styles.iconContainer}
                            iconName='ios-camera'
                            iconSize={48}
                            iconColor='white'
                            onPress={onPress}
                        />
                    </View>
                </Camera>
            </View>
    );
    }
};

export { PaymentCameraReceipt };
