import React, { Component } from "react";
import { Actions } from "react-native-router-flux";

import { connect } from "react-redux";
import { logout } from "../../../marslab-library-react-native/redux/auth/actions";
import onLogOut from "../../../marslab-library-react-native/routes/onLogOut";

import { UserProfile } from "../../../components/templates";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isRefreshing: false,
    };
  }

  componentDidMount() {}

  onSignOut = () => {
    onLogOut().then(() => {
      this.props.logout();
    });
  };

  onEventPress(status) {
    //Actions.JoinedEvent({ status: status });
  }

  onHelpPress() {
    //Actions.Help();
  }

  onEditProfilePress() {
    Actions.EditProfile();
  }

  onSettingsPress() {
    Actions.Settings();
  }

  onAvatarPress() {
    //Actions.ImagePicker();
  }

  handleRefresh() {
    this.setState({ isRefreshing: true });
    this.props
      .listenToUserData()
      .then((data) => {
        this.setState({
          isRefreshing: false,
        });
      })
      .catch((error) => alert(error));
  }

  render() {
    let { user } = this.props;

    return (
      <UserProfile
        user={user}
        onEventPress={this.onEventPress.bind(this)}
        onHelpPress={this.onHelpPress.bind(this)}
        onEditProfilePress={this.onEditProfilePress.bind(this)}
        onSettingsPress={this.onSettingsPress.bind(this)}
        onAvatarPress={this.onSignOut.bind(this)}
        isRefreshing={this.state.isRefreshing}
        refreshHandler={this.handleRefresh.bind(this)}
      />
    );
  }
}

const mapStateToProps = (state) => {
  const user = state.Auth.user;
  return { user };
};

export default connect(mapStateToProps, { logout })(Profile);
