import React from 'react';
import { View, Text, TouchableOpacity, Image } from '../atoms';
import Icon from "react-native-vector-icons/Ionicons";

const ImageInfo = ({
    banner,
    imageContainer,
    imageStyle,
    title,
    subtitle,
    title2,
    subtitle2Part2,
    textStyle,
    textStyle2,
    subtitle2Part1,
    subtitle2Part3,
}) => {
    return (
        <View>
            <View style={imageContainer}>
                <Image
                    style={imageStyle}
                    source={banner && { uri: banner } }
                />
            </View>
            {title &&
                <View>
                    <Text style={textStyle}>
                        {title}{subtitle}
                    </Text>
                    <Text style={textStyle2}>
                        {title2}{subtitle2Part1} {subtitle2Part2}{subtitle2Part3}
                    </Text>
                </View>
            }
        </View>
    );
};

const styles = {

};

export { ImageInfo };
