import React, { Component } from "react";
import {
    View,
    Text,
    StyleSheet,
    Image,
    AppRegistry,
    ScrollView,
    Linking
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import Swiper from "react-native-swiper";
import { Colors } from "../../../../settings/styles/theme";
import moment from "moment";
import styles from './styles';

class index extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    renderOperatingHour() {
        const { 
            subIconDetail, 
            operatingContainer
        } = styles;

        return this.props.merchant.operatingHour.map((item, key) => {
            return (
                <View style={operatingContainer} key={key}>
                    <Ionicons
                        style={subIconDetail, {paddingRight: '5%', paddingLeft: '6%'}}
                        name="md-time"
                        size={20}
                        color='grey'
                    />
                    <Text style={{width: 40}}>
                        {item.day.toUpperCase()}
                    </Text>
                    {item.operate?
                    <Text>
                    {moment(item.open.toString(),"Hmm").format('LT') +
                            " to " +
                            moment(item.close.toString(),"Hmm").format('LT')}
                    </Text> 
                    :
                    <Text style={{marginTop: 5, marginLeft: 10}}>
                        Closed
                    </Text>
                    }
                </View>
            );
        });
    }

    render() {
        const { 
            posterArea, 
            poster, 
            logoPosition, 
            logo, 
            detailArea, 
            title, 
            subTitle, 
            subIconDetailMain, 
            setRow, 
        } = styles;
        let cover = []
        if(this.props.merchant.images.length === 0 )
            cover[0] = require("../../../../../assets/404NotFound800x533.jpg")
        else 
            cover = {uri:(this.props.merchant.images[0])}
    
        let icon = []
        if(!this.props.merchant.logo.length === 0 )
            icon[0] = require("../../../../../assets/logo.png")
        else 
            icon = {uri:(this.props.merchant.logo[0])}
        return (
            <ScrollView>
                <View style={posterArea}>
                    <Image
                        style={poster}
                        source={cover}
                    />
                </View>

                <View style={detailArea}>
                    <Text style={title}>{this.props.merchant.displayTitle}</Text>
                    <View>
                        <View style={setRow}>
                            <Ionicons
                                style={{}}
                                name="md-calendar"
                                size={26}
                                color={Colors.PRIMARY}
                            />
                            <Text
                                style={{
                                        fontSize: 18,
                                        fontWeight: "bold",
                                        marginLeft: 10.5,
                                        //marginRight: 10.5,
                                        marginTop: 3,
                                        color: Colors.PRIMARY,
                                        marginBottom: 5,
                                    }}
                            >
                                Operating Hour
                            </Text>
                        </View>
                        {this.renderOperatingHour()}
                        <View style={setRow}>
                            <Ionicons
                                style={subIconDetailMain}
                                name="ios-call"
                                size={26}
                                color={Colors.PRIMARY}
                            />

                            <Text style={{marginTop: 20}}>
                                {this.props.merchant.phoneNumber}
                            </Text>
                        </View>
                        <View style={setRow}>
                            <Ionicons
                                style={subIconDetailMain}
                                name="ios-pin"
                                size={26}
                                color={Colors.PRIMARY}
                            />
                            <Text style={{marginTop: 20,marginRight:'10%'}}>
                                {this.props.merchant.address.line1} {this.props.merchant.address.line2}
                            </Text>
                        </View>
                    </View>

                    <View>
                        <Text style={subTitle}>DESCRIPTION</Text>
                        <Text
                            style={{
                                marginTop: 20,
                                fontSize: 18,
                                textAlign: 'justify',
                                width: '95%',
                                paddingBottom:10,
                            }}
                        >
                            {this.props.merchant.description}
                        </Text>
                    </View>
                    <View 
                        style={{
                            marginTop: 25, 
                            flexDirection: 'row',
                            justifyContent: 'space-between',
                            alignSelf: 'center',
                            width: '45%',
                            // borderWidth:1,
                        }}>
                        {this.props.merchant.facebookUrl !== '' &&
                        <Ionicons
                            style={{}}
                            name="logo-facebook"
                            size={30}
                            color={Colors.PRIMARY}
                            onPress={ ()=>{ Linking.openURL(this.props.merchant.facebookUrl)}}
                        />}
                        {this.props.merchant.instagramUrl !== '' &&
                        <Ionicons
                            style={{}}
                            name="logo-instagram"
                            size={30}
                            color={Colors.PRIMARY}
                            onPress={ ()=>{ Linking.openURL(this.props.merchant.instagramUrl)}}
                        />}
                        {this.props.merchant.whatsapp !== '' &&
                        <Ionicons
                            style={{}}
                            name="logo-whatsapp"
                            size={30}
                            color={Colors.PRIMARY}
                            onPress={ ()=>{ Linking.openURL('https://wa.me/'+this.props.merchant.whatsapp)}}
                        />}
                        {this.props.merchant.websiteUrl !== '' &&
                        <Ionicons
                            style={{}}
                            name="md-link"
                            size={30}
                            color={Colors.PRIMARY}
                            onPress={ ()=>{ Linking.openURL(this.props.merchant.websiteUrl)}}
                        />}
                    </View>
                    <View style={logoPosition}>
                        <Image
                            style={logo}
                            source={icon}
                        />
                    </View>
                </View>
                <View style={{ height: 100 }} />
            </ScrollView>
        );
    }
}

AppRegistry.registerComponent("myproject", () => Swiper);

export default index;
