import { Notifications } from "expo";
import { notification as customNotification } from "../../../../settings";

export default function notificationChannelInit() {
  if (Platform.OS === "android") {
    Notifications.createChannelAndroidAsync("default", {
      name: "Default",
      //description: "Default notification",
      priority: "max", //min | low | default | high | max
      sound: true,
      vibrate: [250, 250, 250, 250], // boolean or array
      //badge : true,   //boolean
    });

    customNotification.notificationChannel.forEach((channel) => {
      const {
        id,
        name,
        description,
        priority,
        sound,
        vibrate,
        badge,
      } = channel;

      Notifications.createChannelAndroidAsync(id, {
        name,
        description,
        priority,
        sound,
        vibrate,
        badge,
      });
    });
  }
}
