import actions from "./action";

const initialState = {
  readLoading: false,
  readError: false,

  // submitLoading: false,
  // submitError: {
  //   code: null,
  //   message: null,
  //   details: null
  // },
  // submitResult: {
  //   objectName: null,
  //   ids: null,
  //   status: null,
  //   action: null,
  //   message: null
  // },

  notifications: [],
  notification: {
    id: null,

    userListRef: null,
    userIds: [],
    data: {
      objectName: null,
      objectId: null,
      action: null,
    },
    title: null,
    body: null,
    ttl: null,
    expiration: null,
    priority: "default", // 'default' | 'normal'| 'high'

    //ios
    subtitle: null,
    sound: "default", // 'default' | null
    badge: 0,

    //android
    channelId: null,

    created: { at: null, by: null },
    deleted: { at: null, by: null },
    updated: { at: null, by: null },
  },
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case actions.READ_FROM_DATABASE:
      return { ...state, readLoading: true, readError: false };

    case actions.READ_FROM_DATABASE_SUCCESS:
      return {
        ...state,
        readLoading: false,
        notifications: payload.data,
      };

    case actions.READ_FROM_DATABASE_ERROR:
      return { ...state, readLoading: false, readError: payload.error };

    // case actions.SUBMIT_TO_BACKEND:
    //   return {
    //     ...state,
    //     submitLoading: true,
    //     submitError: initialState.submitError,
    //     submitResult: initialState.submitResult,
    //   };
    // case actions.SUBMIT_TO_BACKEND_SUCCESS:
    //   return {
    //     ...state,
    //     submitLoading: false,
    //     submitError: initialState.submitError,
    //     submitResult: payload.data,
    //   };

    // case actions.SUBMIT_TO_BACKEND_ERROR:
    //   return {
    //     ...state,
    //     submitLoading: false,
    //     submitError: payload.error,
    //     submitResult: initialState.submitResult,
    //   };

    case actions.UPDATE:
      return {
        ...state,
        user: payload.data,
      };

    default:
      return state;
  }
};

export default reducer;
