import React, { Component } from "react";
import { connect } from "react-redux";

import {
    View,
    Image,
    Text,
    ScrollView,
} from "../../../../components/atoms";
  
import {
    Linking,
    Alert
} from "react-native";

import {
    listenToRecord as listenFromDatabase,
    removeListenerToRecord as removeListenerFromDatabase,
} from "@redux/shops/action";

import ContentLoader, { Rect } from 'react-content-loader/native'

import { Ionicons } from "@expo/vector-icons";
import { Colors } from "../../../../settings/styles/theme";
import moment from "moment";
import styles from './styles';

class index extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount(){
        const shopId = this.props.shopId;

        this.props.listenFromDatabase({ shopId });
    }

    componentWillUnmount(){
        this.props.removeListenerFromDatabase();
    }

    renderOperatingHour() {
        const { 
            subIconDetail, 
            operatingContainer
        } = styles;

        const {
            shop,
        } = this.props.shopState;

        return shop.operatingHour.map((item, key) => {
            return (
                <View style={operatingContainer} key={key}>
                    <Ionicons
                        style={subIconDetail, {paddingRight: '5%', paddingLeft: '6%'}}
                        name="md-time"
                        size={20}
                        color='grey'
                    />
                    <Text style={{width: 40, fontFamily: "RobotoRegular"}}>
                        {item.day.toUpperCase()}
                    </Text>
                    {item.operate?
                    <Text style={{ marginLeft: 10, fontFamily: "RobotoRegular",}}>
                    {moment(item.open.toString(),"Hmm").format('LT') +
                            " to " +
                            moment(item.close.toString(),"Hmm").format('LT')}
                    </Text> 
                    :
                    <Text style={{ marginLeft: 10, fontFamily: "RobotoRegular",}}>
                        Closed
                    </Text>
                    }
                </View>
            );
        });
    }

    render() {
        const { 
            posterArea, 
            poster, 
            logoPosition, 
            logo, 
            detailArea, 
            title, 
            subTitle, 
            subIconDetailMain, 
            setRow, 
        } = styles;

        const {
            shop,
            readLoading
        } = this.props.shopState;

        let cover = []
        if(shop.images.length === 0 )
            cover = require("../../../../assets/images/404NotFound800x533.jpg")
        else 
            cover = {uri:(shop.images[0])}
        
        let icon = []
        if(shop.logo.length === 0 )
            icon = require("../../../../../assets/logo.png")
        else 
            icon = {uri:(shop.logo[0])}

        if(readLoading){
            return (
                <ContentLoader 
                    speed={1}
                    width={"100%"}
                    height={"100%"}
                    backgroundColor= "#d9d9d9"
                >   
                    <Rect x="0" y="0" rx="0" ry="0" width="100%" height="160" /> 
                    <Rect x="20" y="190" rx="10" ry="10" width="250" height="35" /> 
                    <Rect x="20" y="240" rx="10" ry="10" width="250" height="175" /> 
                    <Rect x="20" y="430" rx="10" ry="10" width="250" height="30" /> 
                    <Rect x="20" y="480" rx="10" ry="10" width="250" height="30" /> 
                </ContentLoader>
            );
        }else {
            return (
                <ScrollView>
                    <View style={posterArea}>
                        <Image
                            style={poster}
                            source={cover}
                        />
                    </View>

                    <View style={detailArea}>
                        <Text style={title}>{shop.displayTitle}</Text>
                        <View>
                            <View style={setRow}>
                                <Ionicons
                                    style={{}}
                                    name="md-calendar"
                                    size={26}
                                    color={Colors.PRIMARY}
                                />
                                <Text
                                    style={{
                                            fontSize: 18,
                                            fontWeight: "bold",
                                            fontFamily: "RobotoRegular",
                                            marginLeft: 10.5,
                                            //marginRight: 10.5,
                                            marginTop: 3,
                                            color: Colors.PRIMARY,
                                            marginBottom: 5,
                                        }}
                                >
                                    Operating Hour
                                </Text>
                            </View>
                            {this.renderOperatingHour()}
                            <View style={setRow}>
                                <Ionicons
                                    style={subIconDetailMain}
                                    name="ios-call"
                                    size={26}
                                    color={Colors.PRIMARY}
                                />

                                <Text 
                                    style={{marginTop: 20, fontFamily: "RobotoRegular"}}
                                    onPress={() => {
                                        Linking.openURL(shop.phoneNumber);
                                    }}
                                >
                                    {shop.phoneNumber}
                                </Text>
                            </View>
                            <View style={setRow}>
                                <Ionicons
                                    style={subIconDetailMain}
                                    name="ios-pin"
                                    size={26}
                                    color={Colors.PRIMARY}
                                />
                                <Text 
                                    style={{marginTop: 20,marginRight:'10%',fontFamily: "RobotoRegular"}}
                                    onPress={() => {
                                        const latitude = shop.l.latitude;
                                        const longitude = shop.l.longitude;
                                        Linking.openURL(`http://www.google.com/maps/place/${latitude},${longitude}`);
                                    }}
                                >
                                    {shop.address.line1} {shop.address.line2} {shop.address.postcode} {shop.address.state}
                                </Text>
                            </View>
                        </View>

                        {shop.description && <View>
                            <Text style={subTitle}>DESCRIPTION</Text>
                            <Text
                                style={{
                                    marginTop: 20,
                                    fontSize: 18,
                                    textAlign: 'justify',
                                    fontFamily: "RobotoRegular",
                                    width: '95%',
                                    paddingBottom:10,
                                }}
                            >
                                {shop.description}
                            </Text>
                        </View>}
                        <View 
                            style={{
                                marginTop: 25, 
                                flexDirection: 'row',
                                justifyContent: 'space-between',
                                alignSelf: 'center',
                                width: '45%',
                                // borderWidth:1,
                            }}>
                                
                            {//shop.facebookUrl !== "" && shop.facebookUrl &&
                            <Ionicons
                                style={{}}
                                name="logo-facebook"
                                size={30}
                                color={Colors.PRIMARY}
                                onPress={ ()=>{ 
                                    if (!shop.facebookUrl) {
                                        return Alert.alert("Sorry, we don't have facebook.");
                                    }
                                    Linking.openURL(shop.facebookUrl)
                                }}
                            />} 
                            {//shop.instagramUrl !== "" && shop.instagramUrl &&
                            <Ionicons
                                style={{}}
                                name="logo-instagram"
                                size={30}
                                color={Colors.PRIMARY}
                                onPress={ ()=>{ 
                                    if (!shop.instagramUrl) {
                                        return Alert.alert("Sorry, we don't have instagram.");
                                    }
                                    Linking.openURL("instagram://user?username=" + shop.instagramUrl)
                                }}
                            />}
                            {//shop.whatsapp !== "" && shop.whatsapp   &&
                            <Ionicons
                                style={{}}
                                name="logo-whatsapp"
                                size={30}
                                color={Colors.PRIMARY}
                                onPress={ ()=>{ 
                                    if (!shop.whatsapp) {
                                        return Alert.alert("Sorry, we don't have whatsapp.");
                                    }
                                    Linking.openURL('https://wa.me/'+shop.whatsapp)
                                }}
                            />}
                            {//shop.websiteUrl !=='' && shop.websiteUrl && 
                            <Ionicons
                                style={{}}
                                name="md-link"
                                size={30}
                                color={Colors.PRIMARY}
                                onPress={ ()=>{ 
                                    if (!shop.websiteUrl) {
                                        return Alert.alert("Sorry, we don't have website.");
                                    }
                                    Linking.openURL('https://'+shop.websiteUrl)
                                }}
                            />}
                        </View>
                        <View style={logoPosition}>
                            <Image
                                style={logo}
                                source={icon}
                            />
                        </View>
                    </View>
                    <View style={{ height: 100 }} />
                </ScrollView>
            );
        }
    }
}

const mapStateToProps = state => {
    const shopState = state.Shops;

    return { shopState };
};

export default connect(mapStateToProps, { listenFromDatabase, removeListenerFromDatabase })(index);
