import React from "react";
import { Scene, Stack } from "react-native-router-flux";

import MainScreen from "../screens/main/MainScreen";
import Shops from "../screens/main/Shops";
import RouteGroups from "../screens/main/RouteGroups";
import Screen4 from "../screens/main/Screen4";
import Profile from "../screens/main/Profile";

import TAC from "../screens/auth/TAC";
import userForm from "../screens/auth/UserForm"

import Shop from "../screens/main/Shops/SingleMerchant";

import Routes from "../screens/main/RouteGroups/Routes";
import Route from "../screens/main/RouteGroups/Route";

import Advertisement from "../screens/main/Advertisement";

import Amount from "../screens/subscreen/payment/Amount";
import PaymentDetail from "../screens/subscreen/payment/Method";
import CameraReceipt from "../screens/subscreen/payment/CameraReceipt";

import styles from "./styles";
import * as icons from "./icons";

const routes = ({isLoggedIn, isBooted, forceUpdate}) => {
  return (
    <Stack hideNavBar={true} initial={isLoggedIn && isBooted && !forceUpdate }>
      <Stack key="main" hideNavBar={true} tabs={true} initial={true}>
        <Scene
          key="mainscreen"
          component={MainScreen}
          title="Main"
          titleStyle={styles.title}
          initial={true}
          icon={icons.emergency}
          hideNavBar={true}
        />
        <Scene
          key="Shops"
          component={Shops}
          title="Shops"
          titleStyle={styles.title}
          initial={false}
          icon={icons.emergency}
        />
        <Scene
          key="RouteGroups"
          component={RouteGroups}
          title="Event"
          titleStyle={styles.title}
          initial={false}
          icon={icons.event}
        />
        <Scene
          key="Profile"
          component={Profile}
          title="Profile"
          titleStyle={styles.title}
          initial={false}
          icon={icons.emergency}
        />
      </Stack>

      <Stack key="SingleMerchant" back>
        <Scene
          key="SingleMerchant"
          component={Shop}
          title="Shop"
          titleStyle={styles.title}
        />
      </Stack>

      <Stack key="EditProfile" back>
        <Scene
          key="EditProfile"
          component={userForm}
          title="Profile"
          titleStyle={styles.title}
        />
      </Stack>

      <Stack key="Routes" back>
        <Scene
          key="Routes"
          component={Routes}
          title="Routes"
          titleStyle={styles.title}
        />
      </Stack>

      <Stack key="Route" back>
        <Scene
          key="Route"
          component={Route}
          title="Route"
          titleStyle={styles.title}
        />
      </Stack>

      <Stack key="Advertisement" back>
        <Scene
          key="Advertisement"
          component={Advertisement}
          title="Advertisement"
          />
      </Stack>
      
      <Stack key="Amount">
          <Scene
              key="Amount"
              component={Amount}
              hideNavBar
          />
      </Stack>

      <Stack key="PaymentDetail" back>
        <Scene
          key="PaymentDetail"
          component={PaymentDetail}
          title="Payment Detail"
          titleStyle={styles.title}
        />
      </Stack>

      <Stack key="CameraReceipt" back>
        <Scene
          key="CameraReceipt"
          component={CameraReceipt}
          title="Pay By Cash"
          titleStyle={styles.title}
        />
      </Stack>
    </Stack>
  );
};


export default routes;
